$(document).ready(function() {
  if ($('.page-about').length) { $('.expand-collapse').click(function() { $(this).prevUntil('h2', '.hidden-content-entry').fadeToggle(300, 'easeInOutCirc'); }); return false; }
  if ($('.page-works').length) {
      $('.page-content-load-spinner').show();
      var _downloadSourcePath = 'download/';
      var _albumartsSourcePath = 'assets/images/albumarts/';
      $.ajax({
          url: 'assets/sitedata/worksdata.xml',
          datatype: 'xml',
          cache: false,
          success: function(data, textStatus, jqXHR) {
              $(data).find('work').each(function() {
                  var $_workData = $(this);
                  var _visible = $_workData.find('visible').text();
                  var _worktype = $_workData.find('worktype').text();
                  var _name = $_workData.find('name').text();
                  var _year = $_workData.find('year').text();
                  var _producer = $_workData.find('producer').text();
                  var _link = $_workData.find('link').text();
                  _worktype = $.trim(_worktype).replace(/\s+/g, '-');
                  if ($('.page-works').find('#' + _worktype).length == 0) { $('.page-works').append('<h2 id="' + _worktype + '" class="generic">' + __localeNamesSwitcher(_worktype) + '</h2><hr class="' + _worktype + '" /><div id="' + _worktype + '-group' + '" class="works-category-group"></div>'); }
                  if (_worktype == 'academic') {
                      var _newWork = '<p><span class="colored-red-text">';
                      _newWork += _year;
                      _newWork += '</span><a href="';
                      _newWork += _downloadSourcePath + _link
                      _newWork += '" target="_blank">';
                      _newWork += _name;
                      _newWork += '</a> <span class="colored-green-text">';
                      _newWork += _producer;
                      _newWork += '</span></p>';
                  } else {
                      var _newWork = '<p><span class="colored-red-text">';
                      _newWork += _year;
                      _newWork += '</span><a href="';
                      _newWork += _albumartsSourcePath + _link
                      _newWork += '" class="works-hover-link" target="_blank">';
                      _newWork += _name;
                      _newWork += ' <span class="colored-green-text">';
                      _newWork += _producer;
                      _newWork += '</span></a></p>';
                  }
                  $(_newWork).appendTo($('#' + _worktype + '-group'));;
                  if (_visible != 'true') { $('#' + _worktype + '-group > p:last').addClass('hidden-content-entry'); }
              });
              $.getScript('assets/scripts/jquery.tinysort.min.js', function() {
                  $('.works-category-group > p').tsort({ order: 'desc' });
                  $('.works-category-group').each(function() { if ($(this).find('p').hasClass('hidden-content-entry')) { $('<p class="expand-collapse">genişlet / daralt</p>').insertAfter($(this).find('p:last-child')).click(function() { $(this).siblings('.hidden-content-entry').fadeToggle(300); }); } });
              });
              $('.works-hover-link').imgPreview({ containerID: 'works-hover-image', imgCSS: { display: 'block', } }).click(function() { return false; });
              $('.works-category-group').css({ display: 'block', opacity: 0 }).delay(75).fadeTo(400, 1);
              $('.page-content').append('<div class="early-scroll-pad"></div>');
              $('.page-content-load-spinner').hide();
              var $_academics = $('#academic-group, #academic, .academic');
              $_academics.hide();
              $('h4 > a').click(function() {
                  var $_clicked = $(this);
                  if ($_clicked.text() == 'Akademik') {
                      $(this).parent().nextAll().not($_academics).css({ 'display': 'none' });
                      $_academics.fadeTo(400, 1);
                  } else {
                      $(this).parent().nextAll().fadeTo(400, 1);
                      $_academics.css({ 'display': 'none' });
                  }
                  return false;
              });
          }
      });
      return false;
  }
  if ($('.page-equipment').length) { $.getScript('assets/scripts/jquery.bezoom.min.js', function() { $('.hover-zoom-image').bezoom({ width: 280, height: 280, marginLeft: 32, marginTop: -80, identifier: 'bezoom-holder-screen', titleSource: 'title', imgSource: 'href', bgColor: '#343434', color: '#ffffff', size: '0.8em', ajaxparent: true }).click(function() { return false; }); }); return false; }
  if ($('.page-links').length) { return false; }
  if ($('.page-contact').length) { $('#contact-social-media-links a').css({ opacity: 0.70 }).hover(function() { $(this).find('img').stop(true, true).animate({ opacity: 1 }, 320).css({ marginTop: -32 }); }, function() { $(this).find('img').stop(true, true).animate({ opacity: 0.7 }, 320).css({ marginTop: 0 }); }); return false; } __initializeWebsiteDOM();
});
$(window).load(function() {});

function __initializeWebsiteDOM() {}

function __localeNamesSwitcher(text) {
  switch (text) {
      case 'album':
          return 'albümler..';
          break;
      case 'arranger':
          return 'aranjör..';
          break;
      case 'performer':
          return 'çaldığı albümler..';
          break;
      case 'stage':
          return 'sahne müzikleri..';
          break;
      case 'series':
          return 'dizi müzikleri..';
          break;
      case 'commercial':
          return 'reklam &amp; jenerik müzikleri..';
          break;
      case 'festivals':
          return 'katıldığı festivaller..';
          break;
      case 'tv':
          return 'televizyon programları..';
          break;
      case 'other':
          return 'diğer..';
          break;
      case 'academic':
          return 'akademik..';
          break;
      default:
          return 'diğer..';
  }
}